
public class SwitchTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char grade = 'A';
		
		switch(grade) {
			case 'A':
				System.out.println("A");
		
			case 'B':
				System.out.println('B');
				
			case 'C':
				System.out.println('C');
			
			default:
				System.out.println("default");
		}
	}

}
